mapper.select:
select id, name, email, age, phone, message, apply_time, state
from t_apply
where id = #{id,jdbcType=INTEGER}

select id, name, email, age, phone, message, apply_time, state
from t_apply
where state = #{state,jdbcType=INTEGER}

select id, name, email, age, phone, message, apply_time, state
from t_apply
where name=#{name} and state=#{state}
